        <div className={s.main_section}>
            <div className={s.clock_holder}>
                <h1  ref={myRef} id="digital">00:00:00</h1>
                <div>
                    {
                        started ?
                            <button id="stop">Start</button>
                            :
                            <button id="start" >Stop</button>
                    }
                    <button id="wait" >Wait</button>
                    <button id="reset" >Reset</button>
                </div>

                <div className={s.stopwatch}>
                    <p>{minutes + ' : ' + seconds + ' : '+ milliseconds}</p>
                    {
                        status ?
                            <button className="btn" onClick={resetHandler}>
                                Stop
                            </button> :
                            <button className="btn" onClick={startHandler}>
                                Start
                            </button>
                    }

                    <button className="btn" onClick={waitHandler}>
                        Wait
                    </button>
                    <button className="btn" onClick={resetHandler}>
                        Reset
                    </button>
                    <Clock time={time}/>

                    {/*/<BtnComponent start={start}
