import s from './App.module.css';
import React, {useState, useEffect} from "react";
import BtnComponent from "./components/BtnComponent";
import Clock from "./components/Clock";
import {delay, debounceTime, map, filter} from "rxjs/operators";
import {fromEvent} from "rxjs";
import * as Observable from "rxjs";
import {useRef} from "react/cjs/react.production.min";


const App = ({ setScoreA, setScoreB }) => {
    const [seconds, setSeconds] = React.useState(0);
    const [milliseconds, setMilliseconds] = React.useState(0);
    const [isRunning, setIsRunning] = React.useState(false);

    React.useEffect(() => {
        if (isRunning) {
            const timerID = setInterval(() => {
                // DO NOT EVER SET INTERVAL TO 1ms!!! It is too fast for CPU and time rendered becomes too slow
                setMilliseconds((ms) => ms + 10);
            }, 10);
            return () => {
                clearInterval(timerID);
            };
        } else {
        }
    }, [isRunning]);

    React.useEffect(() => {
        if (milliseconds === 1000) {
            setSeconds((seconds) => seconds + 1);
            setMilliseconds(0);
        }
    }, [milliseconds]);

    //   Handlers
    const startHandler = () => {
        setIsRunning(!isRunning);
    };

    const resetHandler = () => {
        setIsRunning(false);
        setSeconds(0);
        setMilliseconds(0);
        setScoreA(0);
        setScoreB(0);
    };

    return (
        <div className="stopwatch-container">
            <p>Seconds : {seconds || 0}</p>
            <div className="milliseconds-container">
                <span className="milliseconds-label">Milliseconds :</span>
                <span className="milliseconds-numbers">
          {Math.floor(milliseconds / 10)}
        </span>
            </div>
            <button className="btn" onClick={startHandler}>
                {isRunning ? "Pause" : "Start"}
            </button>
            <button className="btn" onClick={resetHandler}>
                Reset
            </button>
        </div>
    );
};

export default App;
