import s from './App.module.css';
import React, {useState, useEffect} from "react";
import BtnComponent from "./components/BtnComponent";
import Timer from "./components/Timer";
import {interval, of, concat, Subject} from 'rxjs';
import {scan, startWith, takeWhile, repeatWhen, share} from "rxjs/operators";

const START = "START";
const STOP = "STOP";
const WAIT = "WAIT";
const RESET = "RESET"
const initialState = {
    ms: 0,
    s: 0,
    m: 0,
    h: 0,
    status: 0,
    interv: 0
}

function reducer(state = initialState, action){
    switch(action.type){
        case START:
            return {...state, status: 1, interv: setInterval(10)}
        case STOP:
            return {...state, status: 0, interv: clearInterval(state.interv)}
        case WAIT:
            return {...state, status: 2, interv: clearInterval(state.interv)}
        case RESET:
            return {...state, status: 0, interv: clearInterval(state.interv)}
        default:
            return state
    }
}


function App() {
    const [time, setTime] = useState({ms: 0, s: 0, m: 0, h: 0})
    const [interv, setInterv] = useState();
    const [status, setStatus] = useState(0);
    // not started = 0, started = 1, stopped = 2


    const start = () => {
        run();
        setStatus(1);
        setInterv(setInterval(run, 10));
    }
    const stop = () => {
        clearInterval(interv);
        setStatus(0);
        setTime({
            ms: 0,
            s: 0,
            m: 0,
            h: 0
        })
    }
    const wait = () => {
        clearInterval(interv);
        setStatus(2);
    }
    const reset = () => {
        clearInterval(interv);
        setStatus(0);
        setTime({
            ms: 0,
            s: 0,
            m: 0,
            h: 0
        })
    }


    let updatedMs = time.ms,
        updatedS = time.s,
        updatedM = time.m,
        updatedH = time.h;

    const run = () => {
        if(updatedMs === 100){
            updatedS++;
            updatedMs = 0;
        }
        if(updatedS === 60){
            updatedM++;
            updatedS = 0;
        }
        if(updatedM === 60){
            updatedH++;
            updatedM = 0;
        }
        updatedMs++;
        return setTime({
            ms: updatedMs,
            s: updatedS,
            m: updatedM,
            h: updatedH
        })
    }
    return (
        <div className={s.main_section}>
            <div className={s.clock_holder}>
                <div className={s.stopwatch}>
                    <Timer time={time}/>
                    <div>
                        {(status === 0) ?
                            <div>
                                <button className={s.stopwatch_btn + " " + s.stopwatch_btn_gre}
                                        onClick={start}>Start</button>
                                <button className={s.stopwatch_btn + " " + s.stopwatch_btn_red}
                                        onClick={wait}>Wait</button>
                                <button className={s.stopwatch_btn + " " + s.stopwatch_btn_yel}
                                        onClick={stop}>Reset</button>
                            </div> : ""
                        }
                        {(status === 1) ?
                            <div>
                                <button className={s.stopwatch_btn + " " + s.stopwatch_btn_red}
                                        onClick={stop}>Stop</button>
                                <button className={s.stopwatch_btn + " " + s.stopwatch_btn_red}
                                        onClick={wait}>Wait</button>
                                <button className={s.stopwatch_btn + " " + s.stopwatch_btn_yel}
                                        onClick={reset}>Reset</button>
                            </div> : ""
                        }
                        {(status === 2) ?
                            <div>
                                <button className={s.stopwatch_btn + " " + s.stopwatch_btn_gre}
                                        onClick={start}>Start</button>
                                <button className={s.stopwatch_btn + " " + s.stopwatch_btn_red}
                                        onClick={wait}>Wait</button>
                                <button className={s.stopwatch_btn + " " + s.stopwatch_btn_yel}
                                        onClick={reset}>Reset</button>
                            </div> : ""
                        }
                    </div>
                </div>
            </div>
        </div>
    );
}

export default App;
