import s from './App.module.css';
import React, {useState, useEffect} from "react";
import BtnComponent from "./components/BtnComponent";
import Clock from "./components/Clock";
import {timeInterval, delay, debounceTime, map, scan, filter,takeWhile, take, startWith, mergeMap} from "rxjs/operators";
import {fromEvent, interval, of, timer, Subject, Observable} from "rxjs";

const digital = document.getElementById('digital');
const splitsList = document.getElementById('splits-list');

const source = interval(100 /* ms */ );

let started = false;
let time = 0; // 1/10 seconds
const action$ = new Subject();





const subscription = source.subscribe(
    x => {
        if(!started) return;
        time++;
        digital.innerHTML = Math.floor(time / 600) + ":" + Math.floor((time / 10) % 60) + ":" + (time % 10) + "0";
    });

const start = fromEvent(document.getElementById('start'), 'click')
    .subscribe((e) => {
        started = true;
    });

const stop = fromEvent(document.getElementById('stop'), 'click')
    .subscribe((e) => {
        started = false;
    });


const reset = fromEvent(document.getElementById('reset'), 'click')
    .subscribe((e) => {
        started = false;
        time = 0;
        digital.innerHTML = "00:00:00";
        splitsList.innerHTML = "";
    });


const App = () => {
    const [time, setTime] = useState({ms: 0, s: 0, m: 0, h: 0})
    const [interv, setInterv] = useState();
    const [timer, setTimer] = useState({ms: 0, s: 0});
    const [status, setStatus] = useState(false);
    const [milliseconds, setMilliseconds] = useState(0);
    const [seconds, setSeconds] = useState(0);
    const [minutes, setMinutes] = useState(0);
    const [hours, setHoures] = useState(0);
    // not started = 0, started = 1, stopped = 2
    const button = document.querySelector('button');



    useEffect(() => {
        if(status) {
            const id = setInterval(() => {
                setMilliseconds((ms) => ms + 1)
            }, 10);
            return () => {
                clearInterval(id);
            };
        }else{

        }
    }, [status])

    useEffect(() =>{
        if(milliseconds === 100){
            setSeconds((s) => s+1);
            setMilliseconds(0);
        }
    },[milliseconds])

    useEffect(() =>{
        if(seconds === 60){
            setMinutes((m) => m+1);
            setSeconds(0);
        }
    },[seconds])

    const startHandler = () => {
        setStatus(true);
    }
    const waitHandler = () => {
        setStatus(false);
    }
    const resetHandler = () => {
        setStatus(false);
        setMinutes(0)
        setSeconds(0);
        setMilliseconds(0);
    }

    const start = () => {
        run();
        setStatus(1);
        setInterv(setInterval(run, 10));
    }
    const stop = () => {
        clearInterval(interv);
        setStatus(0);
        setTime({
            ms: 0,
            s: 0,
            m: 0,
            h: 0
        })
    }
    const wait = () => {
        clearInterval(interv);
        setStatus(2);
    }
    const reset = () => {
        clearInterval(interv);
        setStatus(0);
        setTime({
            ms: 0,
            s: 0,
            m: 0,
            h: 0
        })
    }

    const run = () => {
        if(time.ms === 100){
            time.s++;
            time.ms = 0;
        }
        if(time.s === 60){
            time.m++;
            time.s = 0;
        }
        if(time.m === 60){
            time.h++;
            time.m = 0;
        }
        time.ms++;
        return setTime({
            ms: time.ms,
            s: time.s,
            m: time.m,
            h: time.h
        })
    }
    return (
        <div className={s.main_section}>
            <div className={s.clock_holder}>
                <h1 id="digital">00:00:00</h1>
                <div>
                    {
                        started ?
                            <button id="start" onClick={() => action$.next('start')}>Stop</button>
                            :
                            <button id="stop" onClick={() => action$.next('stop')}>Start</button>
                    }
                    <button id="wait" onClick={() => action$.next('wait')}>Wait</button>
                    <button id="reset" onClick={() => action$.next('reset')}>Reset</button>
                </div>
                <div className="col right-col" id="splits-list"></div>

                <div className={s.stopwatch}>
                    <p>{minutes + ' : ' + seconds + ' : '+ milliseconds}</p>
                    {
                        status ?
                            <button className="btn" onClick={resetHandler}>
                                Stop
                            </button> :
                            <button className="btn" onClick={startHandler}>
                                Start
                            </button>
                    }

                    <button className="btn" onClick={waitHandler}>
                        Wait
                    </button>
                    <button className="btn" onClick={resetHandler}>
                        Reset
                    </button>
                    <Clock time={time}/>

                    {/*/<BtnComponent start={start}
                                  stop={stop}
                                  wait={wait}
                                  reset={reset}
                                  status={status}/>*/}
                    {/*/<div>
                        {(status === 0) ?
                            <div>
                                <button className={s.stopwatch_btn + " " + s.stopwatch_btn_gre}
                                        onClick={setStatus(1)}>Start</button>
                                <button className={s.stopwatch_btn + " " + s.stopwatch_btn_red}
                                        >Wait</button>
                                <button className={s.stopwatch_btn + " " + s.stopwatch_btn_yel}
                                        onClick={setStatus(0)}>Reset</button>
                            </div> : ""
                        }
                        {(status === 1) ?
                            <div>
                                <button className={s.stopwatch_btn + " " + s.stopwatch_btn_red}
                                        onClick={setStatus(0)}>Stop</button>
                                <button className={s.stopwatch_btn + " " + s.stopwatch_btn_red}
                                        >Wait</button>
                                <button className={s.stopwatch_btn + " " + s.stopwatch_btn_yel}
                                        onClick={setStatus(0)}>Reset</button>
                            </div> : ""
                        }
                        {(status === 0) ?
                            <div>
                                <button className={s.stopwatch_btn + " " + s.stopwatch_btn_gre}
                                        onClick={setStatus(1)}>Start</button>
                                <button className={s.stopwatch_btn + " " + s.stopwatch_btn_red}
                                        >Wait</button>
                                <button className={s.stopwatch_btn + " " + s.stopwatch_btn_yel}
                                        onClick={setStatus(0)}>Reset</button>
                            </div> : ""
                        }
                    </div>*/}
                </div>
            </div>
        </div>
    );
}

const List = ({ items = [], loading = false }) => (
    <ul className={loading ? 'loading' : null}>
        {items.map(item => (
            <li key={item}>{item}</li>
        ))}
    </ul>
);
export default App;
