import s from './App.module.css';
import React, {useState, useEffect} from "react";
import BtnComponent from "./components/BtnComponent";
import Timer from "./components/Timer";
import {interval, of, concat, Subject} from 'rxjs';
import {scan, startWith, takeWhile, repeatWhen, share} from "rxjs/operators";

const [time, setTime] = useState({ms: 0, s: 0, m: 0, h: 0})
const [interv, setInterv] = useState();
const [status, setStatus] = useState(0);
let updatedMs = time.ms,
    updatedS = time.s,
    updatedM = time.m,
    updatedH = time.h;
const count$ = interval(1000).pipe(
    startWith(0, 0, 0, 0),
    scan(() => {
        if (updatedMs === 100) {
            updatedS++;
            updatedMs = 0;
        }
        if (updatedS === 60) {
            updatedM++;
            updatedS = 0;
        }
        if (updatedM === 60) {
            updatedH++;
            updatedM = 0;
        }
        updatedMs++;
        return setTime({
            ms: updatedMs,
            s: updatedS,
            m: updatedM,
            h: updatedH
        })
    })
).pipe(share());
const action$ = new Subject();

const observable$ = concat(count$, of("Wake!")).pipe(
    repeatWhen(() => action$)
)

function App() {

    // not started = 0, started = 1, stopped = 2
    useEffect(() => {
        const sub = observable$.subscribe(setTime);
        return () => sub.unsubscribe()
    },[])

    const start = () => {
        run();
        setStatus(1);
        setInterv(setInterval(run, 10));
    }
    const stop = () => {
        clearInterval(interv);
        setStatus(0);
        setTime({
            ms: 0,
            s: 0,
            m: 0,
            h: 0
        })
    }
    const wait = () => {
        clearInterval(interv);
        setStatus(2);
    }
    const reset = () => {
        clearInterval(interv);
        setStatus(0);
        setTime({
            ms: 0,
            s: 0,
            m: 0,
            h: 0
        })
    }
    const resume = () => start()




    const run = () => {
        if(updatedMs === 100){
            updatedS++;
            updatedMs = 0;
        }
        if(updatedS === 60){
            updatedM++;
            updatedS = 0;
        }
        if(updatedM === 60){
            updatedH++;
            updatedM = 0;
        }
        updatedMs++;
        return setTime({
            ms: updatedMs,
            s: updatedS,
            m: updatedM,
            h: updatedH
        })
    }
    return (
        <div className={s.main_section}>
            <div className={s.clock_holder}>
                <div className={s.stopwatch}>
                    <Timer time={time}/>
                    <div>
                        {(status === 0) ?
                            <div>
                                <button className={s.stopwatch_btn + " " + s.stopwatch_btn_gre}
                                        onClick={start}>Start</button>
                                <button className={s.stopwatch_btn + " " + s.stopwatch_btn_red}
                                        onClick={wait}>Wait</button>
                                <button className={s.stopwatch_btn + " " + s.stopwatch_btn_yel}
                                        onClick={stop}>Reset</button>
                            </div> : ""
                        }
                        {(status === 1) ?
                            <div>
                                <button className={s.stopwatch_btn + " " + s.stopwatch_btn_red}
                                        onClick={stop}>Stop</button>
                                <button className={s.stopwatch_btn + " " + s.stopwatch_btn_red}
                                        onClick={wait}>Wait</button>
                                <button className={s.stopwatch_btn + " " + s.stopwatch_btn_yel}
                                        onClick={reset}>Reset</button>
                            </div> : ""
                        }
                        {(status === 2) ?
                            <div>
                                <button className={s.stopwatch_btn + " " + s.stopwatch_btn_gre}
                                        onClick={resume}>Start</button>
                                <button className={s.stopwatch_btn + " " + s.stopwatch_btn_red}
                                        onClick={wait}>Wait</button>
                                <button className={s.stopwatch_btn + " " + s.stopwatch_btn_yel}
                                        onClick={reset}>Reset</button>
                            </div> : ""
                        }
                    </div>
                </div>
            </div>
        </div>
    );
}

export default App;
