import s from './App.module.css';
import React, {useState, useEffect} from "react";
import BtnComponent from "./components/BtnComponent";
import Clock from "./components/Clock";
import {fromEvent, interval} from "rxjs";



const App = () => {
    const digital = document.getElementById('digital');
    const myRef = React.createRef()

    const source = interval(100 /* ms */ );

    let started = false;
    let time = 0; // 1/10 seconds

    useEffect(() =>{
        const subscription$ = source.subscribe(
            x => {
                if(!started) return;
                time++;
                myRef.current.innerHTML = Math.floor(time / 600) + ":" + Math.floor((time / 10) % 60) + ":" + (time % 10) + "0";
            });
        return () => subscription$.unsubscribe();
    },[started])

    useEffect(()=>{
        const start$ = fromEvent(document.getElementById('start'), 'click')
            .subscribe((e) => {
                started = !started;
                if(started === false){
                    time = 0;
                    myRef.current.innerHTML = "00:00:00";
                }
            });
    },[started])

    useEffect(() => {
        const wait$ = fromEvent(document.getElementById('wait'), 'click')
            .subscribe((e) => {
                started = false;

            });
        return started;
    }, [started])
    console.log(started)
    useEffect(() => {
        const reset$ = fromEvent(document.getElementById('reset'), 'click')
            .subscribe((e) => {
                started = false;
                time = 0;
                myRef.current.innerHTML = "00:00:00";
            });
    },[started])


    return (
        <div className={s.main_section}>
            <div className={s.clock_holder}>
                <h1  ref={myRef} id="digital">00:00:00</h1>
                <div>
                     <button id="start">{started ? 'Stop' : 'Start'}</button>
                    {console.log(started)}
