import s from './App.module.css';
import React, {useState, useEffect} from "react";
import BtnComponent from "./components/BtnComponent";
import Clock from "./components/Clock";
import {delay, debounceTime, map, scan, filter, startWith, mergeMap} from "rxjs/operators";
import {fromEvent, interval, of, Subject} from "rxjs";


const actionMap ={
    increment: +1,
    double: +2,
    decrement: -1
}
const action$ = new Subject();
const update$ = action$.pipe(
    map((action) => {
        if(action === 'increment') return actionMap.increment

        else if(action === 'double') return actionMap.double
        else return actionMap.decrement
    })
)

const count$ = update$.pipe(
    startWith(0),
    scan((count, update) => count + update)
)
const useObservable = observable => {

    // observable = interval(1000);
    const [state, setState] = useState();
    useEffect(() => {
        if(state) {
            const id = setInterval(() => {
                setState((ms) => ms + 1)
            }, 1000);
            return () => {
                clearInterval(id);
            };
        }else{

        }
    }, [state])
    useEffect(() =>{
        if(milliseconds === 100){
            setSeconds((s) => s+1);
            setMilliseconds(0);
        }
    },[milliseconds])

    useEffect(() => {
        const sub = observable.subscribe(setState);
        return () => sub.unsubscribe;
    }, [observable]);
    return state;
}
const App = () => {
    const [time, setTime] = useState({ms: 0, s: 0, m: 0, h: 0})
    const [interv, setInterv] = useState();
    const [timer, setTimer] = useState({ms: 0, s: 0});
    const [status, setStatus] = useState(false);
    const [milliseconds, setMilliseconds] = useState(0);
    const [seconds, setSeconds] = useState(0);
    const [minutes, setMinutes] = useState(0);
    const [hours, setHoures] = useState(0);
    // not started = 0, started = 1, stopped = 2
    const button = document.querySelector('button');
    const count = useObservable(count$)



    useEffect(() => {
        if(status) {
            const id = setInterval(() => {
                setMilliseconds((ms) => ms + 1)
            }, 10);
            return () => {
                clearInterval(id);
            };
        }else{

        }
    }, [status])

    useEffect(() =>{
        if(milliseconds === 100){
            setSeconds((s) => s+1);
            setMilliseconds(0);
        }
    },[milliseconds])

    useEffect(() =>{
        if(seconds === 60){
            setMinutes((m) => m+1);
            setSeconds(0);
        }
    },[seconds])

    const startHandler = () => {
        setStatus(true);
    }
    const waitHandler = () => {
        setStatus(false);
    }
    const resetHandler = () => {
        setStatus(false);
        setMinutes(0)
        setSeconds(0);
        setMilliseconds(0);
    }

    const start = () => {
        run();
        setStatus(1);
        setInterv(setInterval(run, 10));
    }
    const stop = () => {
        clearInterval(interv);
        setStatus(0);
        setTime({
            ms: 0,
            s: 0,
            m: 0,
            h: 0
        })
    }
    const wait = () => {
        clearInterval(interv);
        setStatus(2);
    }
    const reset = () => {
        clearInterval(interv);
        setStatus(0);
        setTime({
            ms: 0,
            s: 0,
            m: 0,
            h: 0
        })
    }

    const run = () => {
        if(time.ms === 100){
            time.s++;
            time.ms = 0;
        }
        if(time.s === 60){
            time.m++;
            time.s = 0;
        }
        if(time.m === 60){
            time.h++;
            time.m = 0;
        }
        time.ms++;
        return setTime({
            ms: time.ms,
            s: time.s,
            m: time.m,
            h: time.h
        })
    }
    return (
        <div className={s.main_section}>
            <div className={s.clock_holder}>
                <button onClick={() => action$.next('decrement')}>-</button>
                <span>{time + " : " + count}</span>

                <button onClick={() => action$.next('increment')}>+</button>
                <button onClick={() => action$.next('double')}>+2</button>


                <div className={s.stopwatch}>
                    <p>{minutes + ' : ' + seconds + ' : '+ milliseconds}</p>
                    {
                        status ?
                            <button className="btn" onClick={resetHandler}>
                                Stop
                            </button> :
                            <button className="btn" onClick={startHandler}>
                                Start
                            </button>
                    }

                    <button className="btn" onClick={waitHandler}>
                        Wait
                    </button>
                    <button className="btn" onClick={resetHandler}>
                        Reset
                    </button>
                    <Clock time={time}/>

                    {/*/<BtnComponent start={start}
                                  stop={stop}
                                  wait={wait}
                                  reset={reset}
                                  status={status}/>*/}
                    {/*/<div>
                        {(status === 0) ?
                            <div>
                                <button className={s.stopwatch_btn + " " + s.stopwatch_btn_gre}
                                        onClick={setStatus(1)}>Start</button>
                                <button className={s.stopwatch_btn + " " + s.stopwatch_btn_red}
                                        >Wait</button>
                                <button className={s.stopwatch_btn + " " + s.stopwatch_btn_yel}
                                        onClick={setStatus(0)}>Reset</button>
                            </div> : ""
                        }
                        {(status === 1) ?
                            <div>
                                <button className={s.stopwatch_btn + " " + s.stopwatch_btn_red}
                                        onClick={setStatus(0)}>Stop</button>
                                <button className={s.stopwatch_btn + " " + s.stopwatch_btn_red}
                                        >Wait</button>
                                <button className={s.stopwatch_btn + " " + s.stopwatch_btn_yel}
                                        onClick={setStatus(0)}>Reset</button>
                            </div> : ""
                        }
                        {(status === 0) ?
                            <div>
                                <button className={s.stopwatch_btn + " " + s.stopwatch_btn_gre}
                                        onClick={setStatus(1)}>Start</button>
                                <button className={s.stopwatch_btn + " " + s.stopwatch_btn_red}
                                        >Wait</button>
                                <button className={s.stopwatch_btn + " " + s.stopwatch_btn_yel}
                                        onClick={setStatus(0)}>Reset</button>
                            </div> : ""
                        }
                    </div>*/}
                </div>
            </div>
        </div>
    );
}

const List = ({ items = [], loading = false }) => (
    <ul className={loading ? 'loading' : null}>
        {items.map(item => (
            <li key={item}>{item}</li>
        ))}
    </ul>
);
export default App;
