import s from './App.module.css';
import React, {useState} from "react";
import BtnDisplayComponent from "./components/BtnDisplayComponent";
import DisplayComponent from "./components/DisplayComponent";

function App() {
    const [time, setTime] = useState({ms: 0, s: 0, m: 0, h: 0})
    const [interv, setInterv] = useState();
    const start = () => {
        run();
        setInterval(run, 10);
    }

    let updatedMs = time.ms,
        updatedS = time.s,
        updatedM = time.m,
        updatedH = time.h;

    const run = () => {
        if(updatedMs === 100){
            updatedS++;
            updatedMs = 0;
        }
        if(updatedS === 60){
            updatedM++;
            updatedS = 0;
        }
        if(updatedM === 60){
            updatedH++;
            updatedM = 0;
        }
        updatedMs++;
        return setTime({
            ms: updatedMs,
            s: updatedS,
            m: updatedM,
            h: updatedH
        })
    }
    return (
        <div className={s.main_section}>
            <div className={s.clock_holder}>
                <div className={s.stopwatch}>
                    <DisplayComponent time={time}/>
                    <BtnDisplayComponent start={start}/>
                </div>
            </div>
        </div>
    );
}

export default App;
