import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import App from './App';
import reportWebVitals from './reportWebVitals';

class TimerWrapper extends React.Component{
    constructor(props){
        super(props);
        this.startTimer = this.startTimer.bind(this);
        this.state = {
            timeLeft: null,
            timer: null
        }
    }
    startTimer(timeLeft){
        let timer = setInterval(()=>{
            let timeLeft = this.state.timeLeft - 1;
            if(timeLeft === 0){
                clearInterval(timer)
            }
            this.setState({
                timeLeft: timeLeft
            })
        }, 1000)
        return this.setState({
            timeLeft: this.timeLeft,
            timer: timer

        })
    }

    render(){
        return(
            <div>
                <h2>Timer</h2>
                <div>
                    <Button time="5" startTimer={this.startTimer}/>
                    <Button time="10" startTimer={this.startTimer}/>
                    <Button time="15" startTimer={this.startTimer}/>
                </div>
                <TimerDisplay timeLeft={this.state.timeLeft} />
            </div>
        )
    }
}

class Button extends React.Component{
    handleStartTimer(){
        return this.props.startTimer(this.props.time)
    }
    render(){
        return(
            <button onClick={this.handleStartTimer.bind(this)}>{this.props.time} sec</button>
        )
    }
}

class TimerDisplay extends React.Component{
    render(){
        if(this.props.timeLeft === 0 || this.props.timeLeft === null){
            return <div></div>
        }
        return <h1>Time Left: {this.props.timeLeft}</h1>
    }
}


ReactDOM.render(
  <React.StrictMode>
    <TimerWrapper />
  </React.StrictMode>,
  document.getElementById('root')
);

