import s from './App.module.css';
import React, {useState, useEffect} from "react";
import BtnComponent from "./components/BtnComponent";
import Clock from "./components/Clock";
import {interval, of, from, concat, Subject, Observable, fromEvent } from 'rxjs';
import {
    map,
    scan,
    startWith,
    takeWhile,
    delay,
    bufferWhen,
    repeatWhen,
    share,
    subscribeOn,
    filter,
    takeUntil,
    tap,
