// )
// const stop$ = action$.pipe(filter(action => action === 'stop'))
//
// const startalarm$ = concat(count$, of("Wake!")).pipe(
//     repeatWhen(() => start$),
// );
//
// const observable$ = concat(startalarm$.pipe(
//     takeUntil(stop$)
// ), of(0));
const button = document.getElementById('start')
const par = document.querySelector('p')


const fromKeydown = (filterKey: string) =>
    fromEvent(document, "keydown").pipe(
        map(e => e.key),
        filter(key => key === filterKey),
        scan((acc, key) => acc + " " + key, "")
    );
let toggle$ = fromEvent(button, 'click')
let source = fromKeydown('a');
source.subscribe(x=>console.log(x))
toggle$.subscribe(x => {
    console.log(x)
    source.fromKeydown('b')
})

const toggleSubject = new Subject();

let source2 = toggleSubject.pipe(
    switchMap(toggle => (toggle ? fromKeydown("b") : fromKeydown("a")))
);

toggle$.subscribe(() => {
    console.log("[imperative]", "listening to b");
    toggleSubject.next(true);
});

function App() {
    const [time, setTime] = useState({ms: 0, s: 0, m: 0, h: 0})
    const [interv, setInterv] = useState();
    const [status, setStatus] = useState(0);
    // not started = 0, started = 1, stopped = 2
    const [state, setState] = useState();


    // useEffect(() => {
    //     const sub = observable$.subscribe(setState);
    //     return () => sub.unsubscribe()
    // }, [])

    const start = () => {
        run();
        setStatus(1);
        setInterv(setInterval(run, 10));
    }
    const stop = () => {
        clearInterval(interv);
        setStatus(0);
        setTime({
            ms: 0,
            s: 0,
            m: 0,
            h: 0
        })
    }
    const wait = () => {
        clearInterval(interv);
        setStatus(2);
    }
    const reset = () => {
        clearInterval(interv);
        setStatus(0);
        setTime({
            ms: 0,
            s: 0,
            m: 0,
            h: 0
        })
    }

    const run = () => {
        if(time.ms === 100){
            time.s++;
            time.ms = 0;
        }
        if(time.s === 60){
            time.m++;
            time.s = 0;
        }
        if(time.m === 60){
            time.h++;
            time.m = 0;
        }
        time.ms++;
        return setTime({
            ms: time.ms,
            s: time.s,
            m: time.m,
            h: time.h
        })
    }
    return (
        <div className={s.main_section}>
            <div className={s.clock_holder}>
                <div className={s.stopwatch}>
                    {/*//<h3>{inter}</h3>*/}
                    <p>{state}</p>
                    <button id="start">Staaart</button>
                    <button >Stop</button>
                    <Clock time={time}/>
                    <BtnComponent start={start} stop={stop} wait={wait} reset={reset} status={status}/>
                </div>
            </div>
        </div>
    );
}

export default App;
