import s from './App.module.css';
import React, {useState, useEffect} from "react";
import BtnComponent from "./components/BtnComponent";
import Clock from "./components/Clock";
import {interval, of, concat, Subject, Observable, fromEvent, } from 'rxjs';
import {scan, startWith, takeWhile, bufferWhen, repeatWhen, share, subscribeOn, filter, takeUntil, tap} from "rxjs/operators";




// const useObservable = observable => {
//     const [time, setTime] = useState({ms: 0, s: 0, m: 0, h: 0})
//     const [interv, setInterv] = useState();
//     const [status, setStatus] = useState(0);
//
//     useEffect(() => {
//         const sub = observable.subscribe(setStatus);
//         return () => sub.unsubscribe()
//     }, [observable]);
//
//     return status
// }
const count$ = interval(500).pipe(
    startWith(0),
    scan(time => time + 1),
    takeWhile(time => time < 5)
);
const action$ = new Subject();
const start$ = action$.pipe(filter(action => action === 'start'),
    tap(ev => console.log(ev))
)
const stop$ = action$.pipe(filter(action => action === 'stop'))

const startalarm$ = concat(count$, of("Wake!")).pipe(
    repeatWhen(() => start$),
);

const observable$ = concat(startalarm$.pipe(
    takeUntil(stop$)
), of(0));

function App() {
    const [time, setTime] = useState({ms: 0, s: 0, m: 0, h: 0})
    const [interv, setInterv] = useState();
    const [status, setStatus] = useState(0);
    // not started = 0, started = 1, stopped = 2
    const [state, setState] = useState();
    const button = document.getElementById('start')
    const par = document.querySelector('p')

    const click = fromEvent(button, 'click');

    const buffered = click.pipe(
        delay(250),
        filter(events => events.length >= 2),

    )
    buffered.subscribe((res) => {
        par.textContent = "Math.random()";
    })

    const start = () => {
        run();
        setStatus(1);
        setInterv(setInterval(run, 10));
    }
    const stop = () => {
        clearInterval(interv);
        setStatus(0);
        setTime({
            ms: 0,
            s: 0,
            m: 0,
            h: 0
        })
    }
    const wait = () => {
        clearInterval(interv);
        setStatus(2);
    }
    const reset = () => {
        clearInterval(interv);
        setStatus(0);
        setTime({
            ms: 0,
            s: 0,
            m: 0,
            h: 0
        })
    }

    const run = () => {
        if(time.ms === 100){
            time.s++;
            time.ms = 0;
        }
        if(time.s === 60){
            time.m++;
            time.s = 0;
        }
        if(time.m === 60){
            time.h++;
            time.m = 0;
        }
        time.ms++;
        return setTime({
            ms: time.ms,
            s: time.s,
            m: time.m,
            h: time.h
        })
    }
    return (
        <div className={s.main_section}>
            <div className={s.clock_holder}>
                <div className={s.stopwatch}>
                    {/*//<h3>{inter}</h3>*/}
                    <p></p>
                    <button id="start" onClick={() => action$.next('start')}>Staaart</button>
                    <button onClick={() => action$.next('stop')}>Stop</button>
                    <Clock time={time}/>
                    <BtnComponent start={start} stop={stop} wait={wait} reset={reset} status={status}/>
                </div>
            </div>
        </div>
    );
}

export default App;
