import s from './App.module.css';
import React, {useState, useEffect} from "react";
import BtnComponent from "./components/BtnComponent";
import Clock from "./components/Clock";
import {delay, debounceTime, map, filter} from "rxjs/operators";
import {fromEvent} from "rxjs";
import * as Observable from "rxjs";
import {useRef} from "react/cjs/react.production.min";


function App() {
    const [time, setTime] = useState({ms: 0, s: 0, m: 0, h: 0})
    const [interv, setInterv] = useState();
    const [status, setStatus] = useState(false);
    const [miliseconds, setMiliseconds] = useState(0);
    const [seconds, setSeconds] = useState(0);
    const [minutes, setMinutes] = useState(0);
    const [hours, setHoures] = useState(0);
    // not started = 0, started = 1, stopped = 2
    const button = document.querySelector('button');

    useEffect(() => {
        if(status){
            const id = window.setInterval(() => {
                setMiliseconds(miliseconds => miliseconds+1)
            }, 100);
            return () => window.clearInterval(id);
        }
        return undefined
    }, [status])



    const start = () => {
        run();
        setStatus(1);
        setInterv(setInterval(run, 10));
    }
    const stop = () => {
        clearInterval(interv);
        setStatus(0);
        setTime({
            ms: 0,
            s: 0,
            m: 0,
            h: 0
        })
    }
    const wait = () => {
        clearInterval(interv);
        setStatus(2);
    }
    const reset = () => {
        clearInterval(interv);
        setStatus(0);
        setTime({
            ms: 0,
            s: 0,
            m: 0,
            h: 0
        })
    }

    const run = () => {
        if(time.ms === 100){
            time.s++;
            time.ms = 0;
        }
        if(time.s === 60){
            time.m++;
            time.s = 0;
        }
        if(time.m === 60){
            time.h++;
            time.m = 0;
        }
        time.ms++;
        return setTime({
            ms: time.ms,
            s: time.s,
            m: time.m,
            h: time.h
        })
    }
    return (
        <div className={s.main_section}>
            <div className={s.clock_holder}>
                <div className={s.stopwatch}>
                    <p>{miliseconds}</p>
                    <div>
                        {status ?
                            (<button className={s.stopwatch_btn + " " + s.stopwatch_btn_gre}
                                    onClick={() => setStatus(true)}>Start</button>) :
                            (<button className={s.stopwatch_btn + " " + s.stopwatch_btn_red}
                            onClick={() => setStatus(false)}>Stop</button>)
                        }
                    </div>

                    <button>Click Me</button>
                    <Clock time={time}/>

                    {/*/<BtnComponent start={start}
                                  stop={stop}
                                  wait={wait}
                                  reset={reset}
                                  status={status}/>*/}
